SELECT board.board_no, board_name, horse_hair, title, board.member_no, viewcount, board.content, tel, link, board.regdate, board.update_date, board.isdelete, loc_no, gps_x, gps_y, location.addr, addr2, hos_no, license_no, hospital.park, hospital.ani_type, hurry, room, cent_no, shelter_no, center.park, center.ani_type, (SELECT COUNT(*) FROM file WHERE board.board_no = file.board_no) file_count, nickname AS memb_nickname, (SELECT count(*) FROM recommend WHERE recommend.board_no = board.board_no AND reply_no = 0) rec_count, (SELECT count(*) FROM bookmark WHERE bookmark.board_no = board.board_no) book_count, (SELECT COUNT(*) FROM reply WHERE reply.board_no = board.board_no) reply_count, (SELECT COUNT(*) FROM report WHERE report.board_no = board.board_no AND report.reply_no = 0) report_count FROM board left join location ON board.board_no = location.board_no LEFT JOIN hospital ON board.board_no = hospital.board_no LEFT JOIN center ON board.board_no = center.board_no LEFT JOIN member ON board.member_no = member.member_no
(title LIKE '%${sword}%' OR board.content LIKE '%${sword}%' )
title LIKE '%${sword}%'
board.content LIKE '%${sword}%'
and nickname = #{nickname}
and board_name = #{board_name}
and horse_hair = #{horse_hair}
and board.regdate between #{fromDate} and #{toDate}
and board.board_no = ( SELECT distinct reply.board_no FROM reply WHERE board.board_no = reply.board_no AND reply.content LIKE '%${reply_content}%' )
ORDER BY ${sort} ${order} Limit #{startIdx},${pageRow}

SELECT count(*) FROM board left join location ON board.board_no = location.board_no LEFT JOIN hospital ON board.board_no = hospital.board_no LEFT JOIN center ON board.board_no = center.board_no LEFT JOIN member ON board.member_no = member.member_no
(title LIKE '%${sword}%' OR board.content LIKE '%${sword}%' )
title LIKE '%${sword}%'
board.content LIKE '%${sword}%'
and nickname = #{nickname}
and board_name = #{board_name}
and horse_hair = #{horse_hair}
and board.regdate between #{fromDate} and #{toDate}
and board.board_no = ( SELECT distinct reply.board_no FROM reply WHERE board.board_no = reply.board_no AND reply.content LIKE '%${reply_content}%' )

SELECT *,
	(SELECT COUNT(*) FROM board WHERE board.member_no = member.member_no AND isdelete = 0) AS board_count,
	(SELECT COUNT(*) FROM reply WHERE reply.member_no = member.member_no AND isdelete = 0) AS reply_count
FROM member

WHERE member_id = #{sword1}


WHERE nickname = #{sword1}

ORDER BY ${sort} ${order}
LIMIT #{startIdx},${pageRow}

SELECT r.content, r.member_no, r.regdate, r.gno, r.reply_no, board.horse_hair, board.board_name, board.title, board.board_no, (SELECT count(*) FROM reply WHERE r.reply_no = reply.gno AND reply.ono != 0) reply_count, (SELECT count(*) FROM recommend WHERE recommend.reply_no = r.reply_no) rec_count, (SELECT count(*) FROM report WHERE report.reply_no = r.reply_no) report_count, (SELECT nickname FROM member WHERE member.member_no = r.member_no) nickname FROM reply AS r LEFT JOIN board ON board.board_no = r.board_no LEFT JOIN member ON r.member_no = member.member_no
