mapper.select:
SELECT *
FROM admin



	admin_id LIKE '%${sword}%' OR name LIKE '%${sword}%'


	and ${stype} LIKE '%${sword}%'



ORDER BY ${sort} ${order}
LIMIT #{startIdx},${pageRow}

SELECT *,
	(SELECT COUNT(*) FROM board WHERE board.member_no = member.member_no AND isdelete = 0) AS board_count,
	(SELECT COUNT(*) FROM reply WHERE reply.member_no = member.member_no AND isdelete = 0) AS reply_count
FROM member


ORDER BY (SELECT COUNT(*) FROM board WHERE board.member_no = member.member_no AND isdelete = 0) ${order}


ORDER BY (SELECT COUNT(*) FROM reply WHERE reply.member_no = member.member_no AND isdelete = 0) ${order}


ORDER BY ${sort} ${order}

LIMIT #{startIdx},${pageRow}

SELECT r.content, r.member_no, r.regdate, r.gno, r.reply_no, board.horse_hair, board.board_name, board.title, board.board_no, (SELECT count(*) FROM reply WHERE r.reply_no = reply.gno AND reply.ono != 0) reply_count, (SELECT count(*) FROM recommend WHERE recommend.reply_no = r.reply_no) rec_count, (SELECT count(*) FROM report WHERE report.reply_no = r.reply_no) report_count, (SELECT nickname FROM member WHERE member.member_no = r.member_no) nickname FROM reply AS r LEFT JOIN board ON board.board_no = r.board_no LEFT JOIN member ON r.member_no = member.member_no
title LIKE '%${sword}%' OR board.content LIKE '%${sword}%'
title LIKE '%${sword}%'
board.content LIKE '%${sword}%'
AND nickname = #{nickname}
AND board_name = #{board_name}
AND horse_hair = #{horse_hair}
AND board.regdate between #{fromDate} AND #{toDate}
AND r.content LIKE '%${content}%'
ORDER BY ${sort} ${order} Limit #{startIdx},${pageRow}

SELECT COUNT(*) FROM reply AS r LEFT JOIN board
ON board.board_no = r.board_no
LEFT JOIN member 
ON r.member_no = member.member_no



			 title LIKE '%${sword}%' OR board.content LIKE '%${sword}%'


			 title LIKE '%${sword}%'


			 board.content LIKE '%${sword}%'



		AND nickname = #{nickname}


		AND board_name = #{board_name}

		AND horse_hair = #{horse_hair}



		AND board.regdate between #{fromDate} and #{toDate}


		   AND r.content LIKE '%${content}%'
