mapper.insert:


	SELECT last_insert_id()

INSERT INTO board (
	 board_name, title, content, horse_hair, regdate, tel, link, member_no
) VALUES (
	 #{board_name}, #{title}, #{content}, #{horse_hair}, NOW(), #{tel}, #{link}, #{member_no}
)

mapper.select:
select *, (select nickname from member where board.member_no = member.member_no ) as nickname, (select * from (SELECT filename_real from file where file.board_no = board.board_no order by file_no asc ) as a limit 1 ) as filename_real, (select count(*) from recommend where recommend.board_no = board.board_no ) as rec, (select count(*) from reply where reply.board_no = board.board_no ) as rep from board left join location ON board.board_no = location.board_no where board_name = #{board_name}
AND board.member_no not in (SELECT you_no FROM follow WHERE i_no = #{loginNO} AND relation = 1)
and horse_hair = ${horse_hair}
and addr LIKE '%${sido1}%'
and addr2 LIKE '%${gugun1}%'
and ${stype} Like '%${sword}%'
and (title Like '%${sword}%' or content Like '%${sword}%')
order by rec desc, viewcount desc
order by regdate desc
Limit #{startIdx},${pageRow}

SELECT *,
	(select nickname from member
		 where board.member_no = member.member_no
	) as nickname
from board WHERE board_no = #{board_no}

mapper.update:
UPDATE board SET
title = #{title},
content = #{content}
WHERE board_no = #{board_no}
