mapper.insert:
INSERT INTO center (
	 shelter_no, park, ani_type, board_no
) VALUES (
	 #{shelter_no}, #{park}, #{ani_type}, (SELECT board_no FROM board WHERE board_no = #{board_no})
)

mapper.update:
UPDATE center SET
	shelter_no = #{shelter_no},
	park = #{park},
	ani_type = #{anu_type}
	WHERE cent_no = #{cent_no}

mapper.select:
select *,
	(select nickname from member 
		where board.member_no = member.member_no
	) as nickname,
		(select board_no from board
			where board.board_no = center.board_no
		) as board_no,
			(select * from 
				(SELECT filename_real from file 
					where file.board_no = center.board_no
					order by file_no asc
				) as a limit 1
			) as filename_real
from center
where board_no = #{board_no}
