mapper.insert:

	SELECT last_insert_id()

INSERT INTO board (
	board_no, board_name, title, content
) VALUES (
	#{board_no}, #{board_name}, #{title}, #{content} 
)

INSERT INTO location (
	addr, addr2, gps_x, gps_y, board_no
) VALUES (
	#{addr}, #{addr2}, #{gps_x}, #{gps_y}, (SELECT board_no FROM board WHERE board_no = #{board_no})
)

mapper.update:
UPDATE board SET
title = #{title},
content = #{content}
WHERE board_no = #{board_no}
