INSERT INTO reply (
	board_no, content, member_no,  regdate, update_date, isdelete, gno, ono
	) VALUES (
		#{board_no}, #{content}, #{member_no}, NOW(), NOW(), 0, #{gno}, #{ono}
	)

mapper.select:
SELECT *, (SELECT board_no FROM board WHERE board_no=rp.board_no) AS list_no ,(SELECT nickname FROM member WHERE member_no=rp.member_no) AS member_nickname ,(SELECT COUNT(*) FROM reply re WHERE re.gno = rp.gno AND ono != 0 AND isdelete = 0) AS reply_count ,(SELECT COUNT(*) FROM recommend rec WHERE board_no = #{board_no} AND rec.reply_no = rp.reply_no) AS recommendCount
,(SELECT relation FROM follow WHERE follow.i_no=#{member_no} AND follow.you_no = rp.member_no) AS relation ,(SELECT count(*) FROM recommend reco WHERE member_no = #{member_no} AND board_no = #{board_no} AND reco.reply_no = rp.reply_no) AS recommended
FROM reply rp WHERE board_no = #{board_no} AND ono = 0 ORDER BY regdate ASC LIMIT ${startIdx}, ${pageRow}
