mapper.select:
SELECT report_no, i_no, you_no, reason, report.content, report_date, report.reply_no, report.board_no, stat, resdate, board_name, horse_hair, title as target_title, regdate, update_date, (SELECT count(*) FROM report sub WHERE sub.board_no = report.board_no AND sub.reply_no = report.reply_no ) sumCnt, (SELECT nickname FROM member WHERE member.member_no = i_no) i_nickname, (SELECT nickname FROM member WHERE member.member_no = you_no) you_nickname, (SELECT content FROM reply WHERE reply.reply_no = report.reply_no) target_reply FROM report left join (
select board_no, board_name, horse_hair, title, regdate, update_date, IFNULL('0', '0') reply_no FROM board union select board_no, (SELECT board_name FROM board WHERE board.board_no = reply.board_no) board_name, (SELECT horse_hair FROM board WHERE board.board_no = reply.board_no) horse_hair, content as title, regdate, update_date, reply_no FROM reply
select board_no, board_name, horse_hair, title, regdate, update_date, IFNULL('0', '0') reply_no FROM board
select board_no, (SELECT board_name FROM board WHERE board.board_no = reply.board_no) board_name, (SELECT horse_hair FROM board WHERE board.board_no = reply.board_no) horse_hair, content as title, regdate, update_date, reply_no FROM reply
) b1 ON report.board_no = b1.board_no AND report.reply_no = b1.reply_no
report.reply_no = 0
report.reply_no != 0
AND stat = #{stat}
AND reason = #{reason}
and report.i_no = (SELECT member.member_no FROM member WHERE nickname = #{i_nickname})
and report.you_no = (SELECT member.member_no FROM member WHERE nickname = #{you_nickname})
and report.report_date between #{fromDate} and #{toDate}
and report.report_date between #{resFromDate} and #{resToDate}
ORDER BY ${sort} ${order} Limit #{startIdx},${pageRow}

SELECT COUNT(*) FROM report left join (
select board_no, board_name, horse_hair, title, regdate, update_date, IFNULL('0', '0') reply_no FROM board union select board_no, (SELECT board_name FROM board WHERE board.board_no = reply.board_no) board_name, (SELECT horse_hair FROM board WHERE board.board_no = reply.board_no) horse_hair, content as title, regdate, update_date, reply_no FROM reply
select board_no, board_name, horse_hair, title, regdate, update_date, IFNULL('0', '0') reply_no FROM board
select board_no, (SELECT board_name FROM board WHERE board.board_no = reply.board_no) board_name, (SELECT horse_hair FROM board WHERE board.board_no = reply.board_no) horse_hair, content as title, regdate, update_date, reply_no FROM reply
) b1 ON report.board_no = b1.board_no AND report.reply_no = b1.reply_no
report.reply_no = 0
report.reply_no != 0
AND stat = #{stat}
AND reason = #{reason}
and report.i_no = (SELECT member.member_no FROM member WHERE nickname = #{i_nickname})
and report.you_no = (SELECT member.member_no FROM member WHERE nickname = #{you_nickname})
and report.report_date between #{fromDate} and #{toDate}
and report.report_date between #{resFromDate} and #{resToDate}
ORDER BY report_no DESC

SELECT *,
	(SELECT count(*) 
	FROM report sub 
	WHERE sub.board_no = report.board_no 
		AND sub.reply_no = report.reply_no 
		AND sub.stat != 'reject') sumCnt
FROM report 
WHERE you_no = #{you_no} 

	AND stat = #{stat}

ORDER BY report_no DESC;

SELECT count(*) sumCnt
FROM report sub 
WHERE you_no = #{you_no}  

	AND stat = #{stat}

mapper.update:
UPDATE board SET
isdelete = 2
WHERE board_no = #{board_no}

UPDATE reply SET
isdelete = 2
WHERE reply_no = #{reply_no}
